mapper.select:
SELECT DISTINCT 1
FROM sys_user
WHERE username = #{username,jdbcType=VARCHAR} OR email = #{email,jdbcType=VARCHAR} OR mobile = #{mobile,jdbcType=VARCHAR}

SELECT password AS "password"
FROM sys_user
WHERE id = #{id,jdbcType=BIGINT}

SELECT CAST(id AS CHAR) AS "id",menu_path AS "path",menu_component AS "component",menu_code AS "name",menu_icon AS "icon",(CASE WHEN menu_status = 0 THEN 1 ELSE 0 END) AS "hideInMenu",CAST(parent_id AS CHAR) AS "parentId" FROM sys_menu WHERE id IN (SELECT menu_id FROM sys_role_menu mrm WHERE EXISTS (SELECT 1 FROM sys_role msr,sys_role_user msru,sys_user msu WHERE msr.id = msru.role_id AND msu.id = msru.user_id AND msr.id = mrm.role_id AND (msu.username = #{username,jdbcType=VARCHAR} OR msu.mobile = #{mobile,jdbcType=VARCHAR})
AND msru.role_id IN (SELECT role_id FROM sys_role_user WHERE post_code = #{postCode,jdbcType=VARCHAR} AND user_id = #{userId,jdbcType=BIGINT})
)) ORDER BY menu_sequence ASC

mapper.update:
UPDATE sys_user
nickname=#{nickname,jdbcType=VARCHAR}, phone=#{phone,jdbcType=VARCHAR}, province_regioncode=#{provinceRegionCode,jdbcType=VARCHAR}, city_regioncode=#{cityRegionCode,jdbcType=VARCHAR}, address=#{address,jdbcType=VARCHAR}, profile=#{profile,jdbcType=VARCHAR}
WHERE id=#{id,jdbcType=BIGINT}

UPDATE sys_user


        password=#{fieldValue,jdbcType=VARCHAR}


        mobile=#{fieldValue,jdbcType=VARCHAR}


        email=#{fieldValue,jdbcType=VARCHAR}


        notify_message=#{fieldValue,jdbcType=TINYINT}


        notify_todo=#{fieldValue,jdbcType=TINYINT}


        avatar=#{fieldValue,jdbcType=VARCHAR}


WHERE id=#{id,jdbcType=BIGINT}
