mapper.select:
SELECT CAST(id AS CHAR) AS "id",dict_name AS "dictName",dict_value AS "dictValue",dict_type AS "dictType",dict_sequence AS "dictSequence",CAST(parent_id AS CHAR) AS "parentId",create_time AS "createTime" FROM sys_dict WHERE 1 = 1
AND parent_id = #{parentId}
ORDER BY dict_sequence ASC

SELECT dict_type AS "dictType"
FROM sys_dict
WHERE id = #{parentId,jdbcType=BIGINT}

SELECT DISTINCT 1
FROM sys_dict
WHERE dict_type = #{dictType,jdbcType=VARCHAR}

mapper.update:
UPDATE sys_dict

    dict_name=#{dictName},
    dict_value=#{dictValue},
    parent_id=#{parentId},
    dict_sequence=#{dictSequence,jdbcType=BIGINT}

WHERE id=#{id,jdbcType=BIGINT}
