mapper.select:
SELECT CAST(msm.id AS CHAR) AS "id",msm.menu_code AS "menuCode",msm.menu_name AS "menuName",msm.menu_icon AS "menuIcon",msm.menu_path AS "menuPath",msm.menu_component AS "menuComponent",msm.menu_sequence AS "menuSequence",msm.menu_status AS "menuStatus",(CASE WHEN msm.menu_status = 0 THEN '隐藏' ELSE '显示' END) AS "menuStatusCn",CAST(msm.parent_id AS CHAR) AS "parentId",msm.create_time AS "createTime" FROM sys_menu msm WHERE 1 = 1
AND msm.parent_id = #{parentId}
ORDER BY menu_sequence ASC

SELECT menu_button AS "menuButton"
FROM sys_menu_button
WHERE menu_code = #{menuCode,jdbcType=VARCHAR}

SELECT menu_button AS "menuButton"
FROM sys_role_menubutton
WHERE role_code = #{roleCode,jdbcType=VARCHAR}

SELECT CAST(id AS CHAR) AS "key",role_code AS "roleCode",data_field AS "dataField"
FROM sys_role_data
WHERE menu_code = #{menuCode,jdbcType=VARCHAR}

SELECT menu_button AS "menuButton"
FROM sys_menu_button
WHERE menu_code = #{menuCode,jdbcType=VARCHAR}

SELECT DISTINCT 1
FROM sys_menu
WHERE menu_code = #{menuCode,jdbcType=VARCHAR}

SELECT DISTINCT 1
FROM sys_menu
WHERE menu_path = #{menuPath,jdbcType=VARCHAR}

    AND id != #{id}

mapper.update:
UPDATE sys_menu
menu_name=#{menuName}, menu_status=#{menuStatus}, parent_id=#{parentId}, menu_icon=#{menuIcon,jdbcType=VARCHAR}, menu_path=#{menuPath,jdbcType=VARCHAR}, menu_component=#{menuComponent,jdbcType=VARCHAR}, menu_sequence=#{menuSequence,jdbcType=BIGINT}
WHERE id=#{id,jdbcType=BIGINT}
