mapper.select:
SELECT CAST(msp.id AS CHAR) AS "id",msp.post_code AS "postCode",msp.post_name AS "postName",msp.post_sequence AS "postSequence",CAST(msp.parent_id AS CHAR) AS "parentId",msp.create_time AS "createTime" FROM sys_post msp WHERE 1 = 1
AND msp.parent_id = #{parentId}
ORDER BY post_sequence ASC

      SELECT CAST(id AS CHAR) AS "id",CAST(parent_id AS CHAR) AS "parentId",post_name AS "postName"
      FROM sys_post
	    WHERE 1 = 1

  		AND id = #{id}


  		AND post_code = #{postCode}


  		AND id IN

              #{item}

SELECT DISTINCT 1
FROM sys_post
WHERE post_code = #{postCode,jdbcType=VARCHAR}

mapper.update:
UPDATE sys_post

    post_name=#{postName},
    parent_id=#{parentId},
    post_sequence=#{postSequence,jdbcType=BIGINT}

WHERE id=#{id,jdbcType=BIGINT}

mapper.delete:
		DELETE FROM sys_post
        WHERE id IN

			#{id,jdbcType=BIGINT}
