mapper.select:
      SELECT CAST(msu.id AS CHAR) AS "id",msu.username AS "username",msu.nickname AS "nickname",msu.email AS "email",msu.mobile AS "mobile",msu.prefix AS "prefix",GROUP_CONCAT(msru.role_id) AS "roleId",GROUP_CONCAT(msr.role_name) AS "roleIdCn",CAST(msu.org_id AS CHAR) AS "orgId",mso.org_name AS "orgIdCn",msu.status AS "status",msu.create_time AS "createTime",(SELECT MAX(msup.post_code) FROM sys_user_post msup WHERE msu.id = msup.user_id AND msup.status = 1) AS "postCode"
      FROM sys_user msu LEFT JOIN sys_role_user msru ON msu.id = msru.user_id LEFT JOIN sys_role msr ON msr.id = msru.role_id LEFT JOIN sys_org mso ON msu.org_id = mso.id
	    WHERE 1 = 1

  		AND msu.username REGEXP #{username}


  		AND msu.username LIKE CONCAT('%',#{onlineusername},'%')


  		AND msu.status IN

              #{item}



          AND msu.tenant_code = #{tenantCode}


          AND msu.org_id = #{orgId}

      GROUP BY msu.id

          ORDER BY ${sort} ${sequence}

SELECT username AS "value" FROM sys_user WHERE status = 1
AND id IN
#{id}

SELECT CAST(id AS CHAR) AS "id" FROM sys_user WHERE status = 1
AND username IN
#{username}

SELECT DISTINCT 1
FROM sys_user
WHERE username = #{username,jdbcType=VARCHAR} OR email = #{email,jdbcType=VARCHAR} OR mobile = #{mobile,jdbcType=VARCHAR}

SELECT CONCAT_WS("|",msu.id,msup.post_code) AS 'id',msu.username FROM sys_user msu LEFT JOIN sys_user_post msup ON msu.id = msup.user_id WHERE msu.org_id = #{orgId,jdbcType=BIGINT}
AND NOT EXISTS (SELECT 1 FROM sys_role_user msru WHERE msu.id = msru.user_id AND (msup.post_code = msru.post_code OR msru.post_code IS NULL) AND msru.role_id = #{roleId,jdbcType=BIGINT})
AND EXISTS (SELECT 1 FROM sys_role_user msru WHERE msu.id = msru.user_id AND (msup.post_code = msru.post_code OR msru.post_code IS NULL) AND msru.role_id = #{roleId,jdbcType=BIGINT})

mapper.update:
UPDATE sys_user
password=#{password}, email=#{email}, mobile=#{mobile}, org_id=#{orgId}, status=#{status}, prefix=#{prefix,jdbcType=VARCHAR}
WHERE id=#{id,jdbcType=BIGINT}
