mapper.select:
SELECT CAST(id AS CHAR) AS "id",client_code AS "clientCode",client_secret AS "clientSecret",auth_type AS "authType",auth_scope AS "authScope",token_seconds AS "tokenSeconds",refresh_seconds AS "refreshSeconds",fallback_url AS "fallbackUrl",client_description AS "clientDescription",create_time AS "createTime" FROM app_client WHERE 1 = 1
AND client_code LIKE CONCAT('%',#{clientCode},'%')
AND auth_type LIKE CONCAT('%',#{authType},'%')
AND tenant_code = #{tenantCode}
ORDER BY ${sort} ${sequence}

SELECT DISTINCT 1
FROM app_client
WHERE client_code = #{clientCode,jdbcType=VARCHAR}

    AND id != #{id}

mapper.update:
UPDATE app_client
client_secret=#{clientSecret}, auth_type=#{authType}, auth_scope=#{authScope}, token_seconds=#{tokenSeconds}, refresh_seconds=#{refreshSeconds}, fallback_url=#{fallbackUrl}, client_description=#{clientDescription,jdbcType=VARCHAR},
WHERE id=#{id,jdbcType=BIGINT}
